import { Component } from "react";

class Select extends Component {
  render() {
    const products = this.props.options.map((product) => (
      <option value={product.value}>{product.label}</option>
    ));

    return (
      <select name="select">
        <option value="value1">Apple</option>
        <option value="value2" selected>
          Orange
        </option>
        <option value="value3">Banana</option>
      </select>
    );
  }
}

export default Select;
