// Button.js
import "./Button.css";

const Button = ({ onLogin, isLoggedIn }) => {
  return (
    <button
      className="login-btn"
      onClick={() => {
        if (!isLoggedIn) {
          onLogin();
        }
      }}
      disabled={isLoggedIn} // Вимкнути кнопку при авторизації
    >
      Login
    </button>
  );
};

export default Button;
