import React, { useState } from "react";
import "./Card.css";
import logo from "../../logo.svg";
import Input from "../../components/Input/Input";
import Button from "../../components/Button/Button";

const Card = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [error, setError] = useState("");
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    if (!userName || !password) {
      setError("Обидва поля мають бути заповнені");
      return;
    }

    try {
      // Виконайте відправку запиту на сервер тут
      // За допомогою функції fetch або іншого методу
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username: userName, password: password }),
      });

      if (response.status === 200) {
        const data = await response.json();
        localStorage.setItem("token", data.token);
        setIsLoggedIn(true);
      } else {
        setError("Логін або пароль невірні");
      }
    } catch (error) {
      setError("Помилка під час авторизації");
    }
  };

  return (
    <div className="Login-card">
      <img src={logo} className="App-logo" alt="logo" />
      <Input
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        placeholder="User name"
        showError={error && !userName}
      />
      <Input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
        showError={error && !password}
      />
      <Button onClick={handleLogin} />
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default Card;
