import React, { useState } from "react";
import "./Card.css";
import logo from "../../logo.svg";
import Input from "../../components/Input/Input";
import Button from "../../components/Button/Button";

const Card = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [errors, setErrors] = useState({ userName: "", password: "" });
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    setErrors({ userName: "", password: "" }); // Очищення попередніх помилок

    if (!userName) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        userName: "Введите логин",
      }));
    }

    if (!password) {
      setErrors((prevErrors) => ({
        ...prevErrors,
        password: "Введите пароль",
      }));
    }

    if (!userName || !password) {
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username: userName, password: password }),
      });

      if (response.status === 200) {
        const data = await response.json();
        localStorage.setItem("token", data.token);
        setIsLoggedIn(true);
      } else {
        setErrors({ userName: "Логін або пароль невірні", password: "" });
      }
    } catch (error) {
      setErrors({ userName: "Помилка під час авторизації", password: "" });
    }
  };

  return (
    <div className="Login-card">
      <img src={logo} className="App-logo" alt="logo" />
      <Input
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        placeholder="User name"
        showError={Boolean(errors.userName)}
        errorMessage={errors.userName}
      />
      <Input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Password"
        showError={Boolean(errors.password)}
        errorMessage={errors.password}
      />
      <Button onClick={handleLogin} />
    </div>
  );
};

export default Card;
