import "./Input.css";

const Input = () => {
  const inputStyle = {
    backgroundImage: 'url("./src/components/eye.png")',
    backgroundSize: "20px 20px", // Размер вашей маленькой картинки
    backgroundRepeat: "no-repeat",
    paddingLeft: "30px", // Добавляем отступ слева, чтобы изображение не налазило на текст
  };

  return (
    <div>
      <input type="text" placeholder="User name" />
      <input style={inputStyle} type="text" placeholder="Password" />
    </div>
  );
};

export default Input;
