// Button.js
import "./Button.css";

const Button = ({ onLogin, isLoggedIn }) => {
  const handleLoginClick = () => {
    if (!isLoggedIn) {
      onLogin(); // Викликаємо функцію onLogin, яку передали з Card.js
    }
  };

  return (
    <button
      className="login-btn"
      onClick={handleLoginClick} // Викликаємо нашу функцію обробки події
      disabled={isLoggedIn} // Вимикаємо кнопку, якщо користувач авторизований
    >
      Login
    </button>
  );
};

export default Button;
