import React, { useState } from "react";
import "./Input.css";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const Input = ({ type = "text", placeholder, value, onChange, showError }) => {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="login-input">
      <input
        type="text"
        placeholder="User name"
        value={userName}
        onChange={(e) => {
          setUserName(e.target.value);
          setUserNameError(""); // Очищення повідомлення про помилку при введенні
        }}
      />
      {userNameError && <div className="error-message">{userNameError}</div>}
      <input
        type={showPassword ? "text" : "password"}
        placeholder="Password"
        value={password}
        onChange={(e) => {
          setPassword(e.target.value);
          setPasswordError(""); // Очищення повідомлення про помилку при введенні
        }}
      />
      <div className="eyeIcon" onClick={() => setShowPassword(!showPassword)}>
        {showPassword ? <FaEyeSlash /> : <FaEye />}
      </div>
      {passwordError && <div className="error-message">{passwordError}</div>}
    </div>
  );
};

export default Input;
