import React, { useState } from "react";
import "./Card.css";
import logo from "../../logo.svg";
import Input from "../../components/Input/Input";

const Card = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (userName, password) => {
    // Ось де ви можете виконати логіку авторизації і збереження в LocalStorage
    // Після успішної авторизації встановіть isLoggedIn в true
    setIsLoggedIn(true);
  };

  return (
    <div className="Login-card">
      <img src={logo} className="App-logo" alt="logo" />
      {isLoggedIn ? <p>You are logged in!</p> : <Input onLogin={handleLogin} />}
    </div>
  );
};

export default Card;
