// Input.js

import React, { useState } from "react";
import "./Input.css";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const Input = ({ type = "text", placeholder, value, onChange }) => {
  const [showPassword, setShowPassword] = useState(false);

  const showError = !value && placeholder; // Проверяем, нужно ли показывать ошибку

  return (
    <div className={`login-input ${showError ? "error" : ""}`}>
      <input
        type={showPassword ? "text" : type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
      />
      {type === "password" && (
        <div className="eyeIcon" onClick={() => setShowPassword(!showPassword)}>
          {showPassword ? <FaEyeSlash /> : <FaEye />}
        </div>
      )}
      {showError && (
        <div className="error-message">{`Введите ${placeholder.toLowerCase()}`}</div>
      )}
    </div>
  );
};

export default Input;
